import{_ as e,a as t,b as a,c as r,d as i,e as n,f as o}from"./chunks/artivactvr-poc-demo-six.BKIV2VtZ.js";import{_ as s,c as p,o as c,a2 as m}from"./chunks/framework.BverbYjs.js";const P=JSON.parse('{"title":"Artivact im Metaverse","description":"","frontmatter":{},"headers":[],"relativePath":"de/experience/about.md","filePath":"de/experience/about.md"}'),l={name:"de/experience/about.md"},d=m('<h1 id="artivact-im-metaverse" tabindex="-1">Artivact im Metaverse <a class="header-anchor" href="#artivact-im-metaverse" aria-label="Permalink to &quot;Artivact im Metaverse&quot;">​</a></h1><h2 id="start" tabindex="-1">Start <a class="header-anchor" href="#start" aria-label="Permalink to &quot;Start&quot;">​</a></h2><div class="danger custom-block"><p class="custom-block-title">In Bearbeitung</p><p>ArtivactVR wird gerade entwickelt und ist noch in einem Alpha-Stadium.</p></div><p>Virtuelle Ausstellungsstücke und ihre 3D-Modelle bleiben trotz allem Aufwand auf Bildschirmen flach. Sie in VR/AR zu erleben fügt ihnen eine völlig neue Dimension hinzu.</p><p>ArtivactVR ist die VR app für mobile VR-Headsets. Es wird mithilfe der Open-Source Gameengine <a href="https://godotengine.org/" target="_blank" rel="noreferrer">Godot</a> entwickelt.</p><p>Aktuell steht noch kein Release der app zur Verfügung. Diese Dokumentation wird mit dem weiteren Projektfortschritt aktualisiert.</p><h2 id="proof-of-concept" tabindex="-1">Proof-of-Concept <a class="header-anchor" href="#proof-of-concept" aria-label="Permalink to &quot;Proof-of-Concept&quot;">​</a></h2><p>Aktuell steht eine &quot;Proof-of-Concept&quot;-Version der Anwendung zum Testen bereit. Die APKs könne hier heruntergeladen werden:</p><p><a href="https://www.arassec.com/downloads/ArtivactVR-Quest_POC.apk" target="_blank" rel="noreferrer">ArtivactVR-POC Quest(2/3) APK</a> (md5:9cd0e2b0775871efc9fe733af5faf0fd)</p><p><a href="https://www.arassec.com/downloads/ArtivactVR-Pico4_POC.apk" target="_blank" rel="noreferrer">ArtivactVR-POC Pico4 APK</a> (md5:c81b26b63937546a829ebcc7ea5a93eb)</p><h3 id="impressionen" tabindex="-1">Impressionen <a class="header-anchor" href="#impressionen" aria-label="Permalink to &quot;Impressionen&quot;">​</a></h3><p>Die app bietet aktuell nur Basisfunktionalität um mit Artivact erstellte Ausstellungen zu präsentieren. Der Hauptzweck ist die Erbringung des Beweises, dass per Fotogrammetrie erstellte 3D-Modell auf mobilen VR-Headsets verwendet werden können.</p><p>Ein kurzes Demo-Video (ohne Sound oder Kommentare) steht zum Download zur Verfügung:</p><p><a href="https://www.arassec.com/downloads/artivactvr-poc-demo-video.mp4" target="_blank" rel="noreferrer">artivactvr-poc-demo-video.mp4</a></p><p>Um einen ersten Eindruck der App zu bekommen, stehen hier ein paar Screenshots zur Verfügung:</p><p><img src="'+e+'" alt="main menu"></p><p><img src="'+t+'" alt="main menu"></p><p><img src="'+a+'" alt="main menu"></p><p><img src="'+r+'" alt="main menu"></p><p><img src="'+i+'" alt="main menu"></p><p><img src="'+n+'" alt="main menu"></p><p><img src="'+o+'" alt="main menu"></p>',22),u=[d];function h(f,_,b,g,k,v){return c(),p("div",null,u)}const V=s(l,[["render",h]]);export{P as __pageData,V as default};
