import{_ as t,a as r,b as n,c as a}from"./chunks/blender-logo.Bz5n69V6.js";import{_ as i,c as l,o,ae as s}from"./chunks/framework.CeIuOzFV.js";const k=JSON.parse('{"title":"Artivact als lokale Anwendung","description":"","frontmatter":{},"headers":[],"relativePath":"de/quick-start/local-installation/introduction.md","filePath":"de/quick-start/local-installation/introduction.md"}'),d={name:"de/quick-start/local-installation/introduction.md"};function u(h,e,m,g,c,b){return o(),l("div",null,[...e[0]||(e[0]=[s('<h1 id="artivact-als-lokale-anwendung" tabindex="-1">Artivact als lokale Anwendung <a class="header-anchor" href="#artivact-als-lokale-anwendung" aria-label="Permalink to &quot;Artivact als lokale Anwendung&quot;">​</a></h1><h2 id="uber" tabindex="-1">Über <a class="header-anchor" href="#uber" aria-label="Permalink to &quot;Über&quot;">​</a></h2><p>Artivact kann dir helfen, deine Sammlung zu erstellen und zu organisieren. Auf dem Desktop ist es außerdem in der Lage, mithilfe von Drittanbieter-Tools für Photogrammetrie 3D-Modelle deiner Objekte zu erzeugen.</p><p>Die Nutzung von Photogrammetrie zur Erstellung digitaler Zwillinge realer Objekte ist ein zeitaufwändiger und mühsamer Prozess. Dabei entstehen oft unzählige Dateien und Verzeichnisse im Dateisystem, sodass man leicht den Überblick über den Fortschritt verlieren kann.</p><p>Der Prozess der Modellerstellung umfasst in der Regel die folgenden Schritte:</p><ul><li>Aufnahme vieler Bilder des Objekts aus verschiedenen Perspektiven.</li><li>Kopieren der Bilder von der Kamera auf den PC.</li><li>Nutzung der Fotos in einem Photogrammetrie-Tool zur Erstellung eines 3D-Modells.</li><li>Gelegentliches Bearbeiten des Ergebnisses in einem Modell-Editor, um es fertigzustellen.</li><li>Exportieren des Modells in das gewünschte Format.</li></ul><p>Artivact ermöglicht die Konfiguration von Drittanbieterprogrammen sowie die Nutzung eines Kamerasetups, das mit deinem PC verbunden ist, und beschleunigt so den gesamten Prozess – vom Fotografieren bis zur Modellerstellung und zum Export – erheblich.</p><h2 id="funktionen" tabindex="-1">Funktionen <a class="header-anchor" href="#funktionen" aria-label="Permalink to &quot;Funktionen&quot;">​</a></h2><p>Die Desktop-Anwendung bietet die folgenden Funktionen:</p><ul><li>Flexible Möglichkeiten zur Organisation deiner Sammlung und zur Nachverfolgung jedes virtuellen Objekts.</li><li>Einfache und schnelle Erstellung von 3D-Modellen durch die Kombination verschiedener Werkzeuge im Erstellungsprozess.</li><li>Export erstellter Objekte und ihrer Mediendateien in einem standardisierten ZIP-Format.</li></ul><h2 id="drittanbieter-software" tabindex="-1">Drittanbieter-Software <a class="header-anchor" href="#drittanbieter-software" aria-label="Permalink to &quot;Drittanbieter-Software&quot;">​</a></h2><p>Artivact selbst ist keine Photogrammetrie-Software. Es stellt ein Frontend für verschiedene im Prozess benötigte Tools bereit, ohne diese vor dem Benutzer zu verbergen. Der Benutzer behält die volle Kontrolle über die eingesetzten Tools, erhält jedoch Unterstützung bei deren Handhabung.</p><p>Die folgende Software wird derzeit unterstützt.</p><h3 id="modellerstellung" tabindex="-1">Modellerstellung <a class="header-anchor" href="#modellerstellung" aria-label="Permalink to &quot;Modellerstellung&quot;">​</a></h3><p>Die folgenden Photogrammetrie-Tools werden von Artivact unterstützt. Sie werden aus dem Programm heraus gestartet, müssen aber vom Benutzer manuell bedient werden:</p><table tabindex="0"><thead><tr><th style="text-align:center;">Windows &amp; Linux</th><th style="text-align:center;">Windows &amp; Linux</th><th style="text-align:center;">Windows</th></tr></thead><tbody><tr><td style="text-align:center;"><a href="https://alicevision.org/#meshroom" target="_blank" rel="noreferrer">Meshroom</a> <img src="'+t+'" alt="Meshroom"></td><td style="text-align:center;"><a href="https://www.agisoft.com/" target="_blank" rel="noreferrer">Metashape</a> <img src="'+r+'" alt="Metashape"></td><td style="text-align:center;"><a href="https://www.realityscan.com" target="_blank" rel="noreferrer">RealityScan</a> <img src="'+n+'" alt="RealityScan"></td></tr></tbody></table><h3 id="modellbearbeitung" tabindex="-1">Modellbearbeitung <a class="header-anchor" href="#modellbearbeitung" aria-label="Permalink to &quot;Modellbearbeitung&quot;">​</a></h3><p>Zur Bearbeitung des erstellten 3D-Modells kann folgendes Tool verwendet werden:</p><table tabindex="0"><thead><tr><th style="text-align:center;">Windows &amp; Linux</th></tr></thead><tbody><tr><td style="text-align:center;"><a href="https://www.blender.org/" target="_blank" rel="noreferrer">Blender 3D</a> <img src="'+a+'" alt="Blender 3D"></td></tr></tbody></table>',19)])])}const w=i(d,[["render",u]]);export{k as __pageData,w as default};
