import{_ as e,c as t,o as r,a2 as a}from"./chunks/framework.DSgG5TdZ.js";const h=JSON.parse('{"title":"Artivact als Web-Server","description":"","frontmatter":{},"headers":[],"relativePath":"de/quick-start/server-installation/introduction.md","filePath":"de/quick-start/server-installation/introduction.md"}'),n={name:"de/quick-start/server-installation/introduction.md"},i=a('<h1 id="artivact-als-web-server" tabindex="-1">Artivact als Web-Server <a class="header-anchor" href="#artivact-als-web-server" aria-label="Permalink to &quot;Artivact als Web-Server&quot;">​</a></h1><h2 id="uber-artivact" tabindex="-1">Über Artivact <a class="header-anchor" href="#uber-artivact" aria-label="Permalink to &quot;Über Artivact&quot;">​</a></h2><p>Artivact kann als Web-Server betrieben werden, um virtuelle Ausstellungsstücke per Browser zu erstellen und zu verwalten.</p><p>Die Anwendung stellt ein einfaches Content-Management-System bereit, um Webseiten per Browser zu erzeugen. Die verfügbaren Widgets, die Bausteine der einzelnen Webseiten, legen ihren Fokus auf die Präsentation der virtuellen Sammlung.</p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><p>Artivact im Web-Server-Modus stellt die folgende Funktionalität bereit:</p><ul><li>Ein einfaches Content-Management-System zur Organisation und Präsentation der Sammlung.</li><li>Management und Verwaltung von virtuellen Ausstellungsstücken via Browser.</li><li>Administrator- und Benutzerkonten zur gemeinsamen Arbeit an der Sammlung.</li><li>Synchronisation mit der Desktop-Variante der Anwendung für den einfachen Upload von neu erstellten Ausstellungsstücken.</li></ul>',7),s=[i];function l(o,u,d,c,v,m){return r(),t("div",null,s)}const b=e(n,[["render",l]]);export{h as __pageData,b as default};
