import{_ as e,a as t,b as r,c as a,d as n,e as i,f as o}from"./chunks/turntable-assembly-final.CIESCvb5.js";import{_ as s,c as l,o as d,a2 as c}from"./chunks/framework.BverbYjs.js";const x=JSON.parse('{"title":"Artivact-Drehteller","description":"","frontmatter":{},"headers":[],"relativePath":"de/tutorials/artivact-as-scanner/artivact-turntable.md","filePath":"de/tutorials/artivact-as-scanner/artivact-turntable.md"}'),u={name:"de/tutorials/artivact-as-scanner/artivact-turntable.md"},m=c('<h1 id="artivact-drehteller" tabindex="-1">Artivact-Drehteller <a class="header-anchor" href="#artivact-drehteller" aria-label="Permalink to &quot;Artivact-Drehteller&quot;">​</a></h1><h2 id="automatische-rotation-von-ausstellungsstucken" tabindex="-1">Automatische Rotation von Ausstellungsstücken <a class="header-anchor" href="#automatische-rotation-von-ausstellungsstucken" aria-label="Permalink to &quot;Automatische Rotation von Ausstellungsstücken&quot;">​</a></h2><p>Um die Objekterzeugung druch Artivact zu beschleunigen, und da die Kamera bereits automatisch gesteuert werden kann, lässt sich auch dei Objektrotation automatisieren.</p><p>Basierend auf anderen &quot;Do-it-Yourself&quot;-3D-Scannern im Internet, bietet Artivact einen steuerbaren Drehteller zur Automatisierung.</p><p>Die Einzelteile müssen per 3D-Drucker ausgedruckt, sonstige Teile zusammengebaut und programmiert werden. Anschließend kann der Drehteller per USB an den Computer angeschlossen werden.</p><h2 id="notwendige-komponenten" tabindex="-1">Notwendige Komponenten <a class="header-anchor" href="#notwendige-komponenten" aria-label="Permalink to &quot;Notwendige Komponenten&quot;">​</a></h2><p>Die folgenden Komponenten werden für den Drehteller benötigt:</p><table><thead><tr><th style="text-align:center;">Komponente</th><th style="text-align:center;">Beschreibung</th><th style="text-align:center;">Downlad/Shop</th></tr></thead><tbody><tr><td style="text-align:center;">Drehteller Einzelteile</td><td style="text-align:center;">Die Einzelteile des Drehtellers, die per 3D-Drucker gedruckt werden müssen.</td><td style="text-align:center;"><a href="https://github.com/arassec/artivact/blob/main/artivact-tt-v1/artivact-tt-v1.zip" target="_blank" rel="noreferrer">Github</a></td></tr><tr><td style="text-align:center;">Schrittmotor 28BYJ-48 + Treiberplatine ULN2003</td><td style="text-align:center;">Schrittmotor um den Drehteller anzutreiben.</td><td style="text-align:center;"><a href="https://www.amazon.com/-/de/dp/B09QQLMYWP/" target="_blank" rel="noreferrer">Amazon</a></td></tr><tr><td style="text-align:center;">Druckkugellager</td><td style="text-align:center;">57x57mm Druckkugellager 360 Grad.</td><td style="text-align:center;"><a href="https://www.amazon.com/-/de/dp/B0BPYR1S1Y/" target="_blank" rel="noreferrer">Amazon</a></td></tr><tr><td style="text-align:center;">Arduino Nano Every</td><td style="text-align:center;">Arduino zur Steuerung des Motors.</td><td style="text-align:center;"><a href="https://store.arduino.cc/products/arduino-nano-every" target="_blank" rel="noreferrer">Arduiono Store</a></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Arduino Sketch</p><p>Der Arduino Nano muss mit folgendem Sketch programmiert werden: <a href="https://github.com/arassec/artivact/blob/main/artivact-tt-v1/sketch_artivact-tt-v1/sketch_artivact-tt-v1.ino" target="_blank" rel="noreferrer">sketch_artivact-tt-v1.ino</a>. Die offizielle Arduino Dokumentation erläutert das notwendige Vorgehen: <a href="https://docs.arduino.cc/learn/starting-guide/getting-started-arduino/" target="_blank" rel="noreferrer">Arduino Documentation</a>.</p></div><h2 id="zusammenbau" tabindex="-1">Zusammenbau <a class="header-anchor" href="#zusammenbau" aria-label="Permalink to &quot;Zusammenbau&quot;">​</a></h2><p>Die 3D-Teile des Drehtellers müssen ausgedruckt und zusammengesteckt werden.</p><p>Den Schrittmotor hinzufügen und mit der Treiberplatine verbinden:</p><p><img src="'+e+'" alt="stepper motor driver wiring"></p><p>Die Treiberplatine mit dem Arduino Nano verbinden und einsetzen:</p><p><img src="'+t+'" alt="driver board arduino wiring"></p><p>Magnete an der Oberplatte des Drehtellers und dem oberen Zahnrad anbringen und die übrigen Teile zusammensetzen:</p><p><img src="'+r+'" alt="assemble top plate"></p><p><img src="'+a+'" alt="assemble top plate"></p><p><img src="'+n+'" alt="assemble top plate"></p><p><img src="'+i+'" alt="assemble top plate"></p><p><img src="'+o+'" alt="assemble top plate"></p><h2 id="konfiguration" tabindex="-1">Konfiguration <a class="header-anchor" href="#konfiguration" aria-label="Permalink to &quot;Konfiguration&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">Linux Benutzer</p><p>Linux Benutzer müssen in der <em>dialout</em> Gruppe sein, um den Artivact-Drehteller benutzen zu können!</p></div><p>Nach dem Zusammenbau kann der Drehteller per USB an den Computer angeschlossen werden und in Artivact aktiviert werden:</p><p><code>Einstellugen</code> -&gt; <code>Peripheriegeräte</code> -&gt; <code>Drehteller-Konfiguration</code> -&gt; <code>Artivact Turntable</code></p>',25),p=[m];function h(g,b,_,k,v,f){return d(),l("div",null,p)}const z=s(u,[["render",h]]);export{x as __pageData,z as default};
