import{_ as e,a as t,b as r}from"./chunks/background-removal_tricked.DcHzrRiI.js";import{_ as n,c as a,o as i,a2 as s}from"./chunks/framework.BverbYjs.js";const _=JSON.parse('{"title":"Bilder Freistellen","description":"","frontmatter":{},"headers":[],"relativePath":"de/tutorials/artivact-as-scanner/background-removal.md","filePath":"de/tutorials/artivact-as-scanner/background-removal.md"}'),o={name:"de/tutorials/artivact-as-scanner/background-removal.md"},d=s('<h1 id="bilder-freistellen" tabindex="-1">Bilder Freistellen <a class="header-anchor" href="#bilder-freistellen" aria-label="Permalink to &quot;Bilder Freistellen&quot;">​</a></h1><h2 id="warum-den-bildhintergrund-entfernen" tabindex="-1">Warum den Bildhintergrund Entfernen? <a class="header-anchor" href="#warum-den-bildhintergrund-entfernen" aria-label="Permalink to &quot;Warum den Bildhintergrund Entfernen?&quot;">​</a></h2><p>Um vollständige, aus jeder Perspektive korrekte 3D-Modelle erstellen zu können, müssen alle Bereiche des Objekts durch Fotos abgedeckt sein. Insbesondere von der Unterseite des Objekts müssen ausreichend Fotos vorhanden sein. Falls diese nicht vorliegen, werden die 3D-Modelle mit Artefakten oder &quot;Löchern&quot; generiert:</p><p><img src="'+e+'" alt="model with hole"></p><p>Da Fotoaufnahmen von der Unterseite schwierig und mühsam zu erstellen sind, kann das Objekt mit der Unterseite nach oben fotografiert, und der Bildhintergrund aus den Fotoaufnahmen entfernt werden.</p><p><img src="'+t+'" alt="image with and without background"></p><p>Durch das Freistellen der Bilder behandelt die Fotogrammetriesoftware diese, als hätte die Kamera das Objekt von der Unterseite fotografiert.</p><p><img src="'+r+'" alt="tricked photogrammetry software"></p><p>Artivact kann die automatisch erstellten Aufnahmen direkt freistellen, und das Ergebnis zur Modellerstellung bereitstellen.</p><div class="warning custom-block"><p class="custom-block-title">Nachteile</p><p>Der Nachteil der Manipulation der Bilder ist, dass Metainformationen der Kamera aus den freigestellten Bildern entfernt werden. Dies kann dazu führen, dass einige Fotogrammetrie-Anwendungen diese langsamer oder ggf. gar nicht mehr verarbeiten können. Meshroom und Metashape haben diesbezüglich bisher keine Probleme gezeigt.</p></div><h2 id="rembg" tabindex="-1">rembg <a class="header-anchor" href="#rembg" aria-label="Permalink to &quot;rembg&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">Unterstütze Version</p><p>Die rembg-Integration wurde mit der Version 2.0 von rembg getestet.</p></div><p>Artivact kann für das automatische Freistellen das Open-Source-Tool <a href="https://github.com/danielgatis/rembg" target="_blank" rel="noreferrer">rembg</a> von Daniel Gatis verwenden.</p><p>Die Software muss mit dem bereitgestellten Docker-Image als Web-Endpunkt installiert werden:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>docker run -d -p 7000:7000 --name=rembg --restart=always danielgatis/rembg s</span></span></code></pre></div><p>Die offizielle <a href="https://docs.docker.com/manuals/" target="_blank" rel="noreferrer">Docker-Dokumentation</a> bietet weitere Informationen, wie das rembg-Docker-Image auf verschiedenen Systemen installiert werden kann.</p><p>Nach der Installation kann das Tool in Artivact konfiguriert werden:</p><p><code>Einstellungen</code> -&gt; <code>Peripheriegeräte</code> -&gt; <code>Automatisches Freistellen</code></p><table><thead><tr><th style="text-align:center;">Selection</th><th style="text-align:center;">Configuration Value</th><th style="text-align:center;">Example</th></tr></thead><tbody><tr><td style="text-align:center;"><code>rembg (Remote)</code></td><td style="text-align:center;">Der Web-API-Endpunkt des Docker-Images.</td><td style="text-align:center;"><code>http://localhost:7000/api/remove</code></td></tr></tbody></table>',19),l=[d];function c(m,h,g,u,p,b){return i(),a("div",null,l)}const v=n(o,[["render",c]]);export{_ as __pageData,v as default};
