import{_ as e}from"./chunks/model-creation-artifact.BcuG0r1t.js";import{_ as t,c as a,o as n,a2 as r}from"./chunks/framework.BverbYjs.js";const g=JSON.parse('{"title":"3D-Modell Bearbeitung","description":"","frontmatter":{},"headers":[],"relativePath":"de/tutorials/artivact-as-scanner/model-editing.md","filePath":"de/tutorials/artivact-as-scanner/model-editing.md"}'),i={name:"de/tutorials/artivact-as-scanner/model-editing.md"},s=r('<h1 id="_3d-modell-bearbeitung" tabindex="-1">3D-Modell Bearbeitung <a class="header-anchor" href="#_3d-modell-bearbeitung" aria-label="Permalink to &quot;3D-Modell Bearbeitung&quot;">​</a></h1><h2 id="warum-das-modell-bearbeiten" tabindex="-1">Warum das Modell Bearbeiten? <a class="header-anchor" href="#warum-das-modell-bearbeiten" aria-label="Permalink to &quot;Warum das Modell Bearbeiten?&quot;">​</a></h2><p>Fotogrammetriesoftware erstellt nicht immer akkurate 3D-Modell aus den aufgenommenen Fotos. Es können Artefakte in das Ergebnis generiert werden, was nicht wünschenswert ist.</p><p><img src="'+e+'" alt="Model with artifact"></p><p>In diesen Fällen kann es nützlich sein, das 3D-Modell von Hand mittels eines 3D-Editor zu bearbeiten.</p><p>Aktuell bietet Artivact eine Integration des freien und Open-Source-Tools Blender 3D für diese Aufgabe.</p><p>Details zur Konfiguration sind im <a href="./../../user-manual/settings/peripherals.html">Benutzerhandbuch</a> beschrieben.</p><p>Das Finale 3D-Modell sollte als GLTF/GLB-Datei in das von Blender vorgeschlagene Exportverzeichnis exportiert werden.</p><p>Diese kann nun über die Detailansicht des Model-Sets in den Medienbereich des Ausstellungsstückes übertragen und veröffentlicht werden.</p>',9),l=[s];function d(o,c,u,m,h,p){return n(),a("div",null,l)}const f=t(i,[["render",d]]);export{g as __pageData,f as default};
