import{_ as t,c as n,o as i,ae as l}from"./chunks/framework.CeIuOzFV.js";const a="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAADgAAAAkCAIAAABT8G6pAAAAeElEQVR4nOzUsQqAIBRGYRNft4ZoDCIagyB6X01oKRw7g3L/bxB0OijeMO+nq1P67IJrhEJpCqUplKZQmuHQdezzuhyXQ+npaQqlmQnNf9z7rjzfpuG9jTH9nANmbrS8J83RRiiUZjgU/+8PPT1NoTSF0hRKUyjtBgAA//+60BaFAAAABklEQVQDADutE83E0iViAAAAAElFTkSuQmCC",d="data:image/png;base64,",c=JSON.parse('{"title":"Menüs","description":"","frontmatter":{},"headers":[],"relativePath":"de/user-manual/content-management/menus.md","filePath":"de/user-manual/content-management/menus.md"}'),r={name:"de/user-manual/content-management/menus.md"};function s(g,e,A,o,u,h){return i(),n("div",null,[...e[0]||(e[0]=[l('<h1 id="menus" tabindex="-1">Menüs <a class="header-anchor" href="#menus" aria-label="Permalink to &quot;Menüs&quot;">​</a></h1><h2 id="menuverwaltung" tabindex="-1">Menüverwaltung <a class="header-anchor" href="#menuverwaltung" aria-label="Permalink to &quot;Menüverwaltung&quot;">​</a></h2><p>Du kannst Menüs hinzufügen, indem du in der oberen Leiste auf den folgenden Button links klickst: <img src="'+a+'" alt="add-menu-button"></p><p>Menüs können nur hinzugefügt werden, wenn die Standardsprache (default locale) ausgewählt ist!</p><p>Es öffnet sich ein Menü mit den folgenden Optionen: <img src="'+d+'" alt="add-menu-menu"></p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Beschreibung</th></tr></thead><tbody><tr><td style="text-align:left;">Menü erstellen</td><td style="text-align:left;">Öffnet einen Dialog, um ein neues Menü zu erstellen.</td></tr><tr><td style="text-align:left;">Menü importieren</td><td style="text-align:left;">Importiert ein zuvor exportiertes Menü in die Menüleiste.</td></tr></tbody></table><p>Der Dialog zum Erstellen/Bearbeiten eines Menüs bietet folgende Optionen:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Beschreibung</th></tr></thead><tbody><tr><td style="text-align:left;">Menü</td><td style="text-align:left;">Der Name des Menüs, der in der Menüleiste angezeigt wird.</td></tr><tr><td style="text-align:left;">Einschränkungen</td><td style="text-align:left;">Menüs können auf Administratoren und/oder Benutzer beschränkt werden. Das Menü wird nur angezeigt, wenn der angemeldete Benutzer die entsprechende Rolle besitzt.</td></tr><tr><td style="text-align:left;">Externe Seiten-URL</td><td style="text-align:left;">Ein Menü kann auf eine externe URL statt auf eine interne Seite verweisen.</td></tr><tr><td style="text-align:left;">URL-Alias</td><td style="text-align:left;">Wenn ein Menü auf eine Artivact-Seite zeigt und nicht auf eine externe URL, kann ein Alias definiert werden. Dieser Alias kann in der Browser-URL anstelle der Seiten-ID verwendet werden.</td></tr><tr><td style="text-align:left;">Verstecktes Menü</td><td style="text-align:left;">Ein Menü und seine zugehörige Seite können öffentlich zugänglich gemacht werden, d. h. ohne Einschränkungen, aber aus der Menüleiste ausgeblendet.</td></tr></tbody></table><p>Durch Rechtsklick auf das Menü öffnet sich ein Kontextmenü mit folgenden Optionen:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Beschreibung</th></tr></thead><tbody><tr><td style="text-align:left;">Menü bearbeiten</td><td style="text-align:left;">Öffnet den Menü-Dialog und erlaubt die Änderung des Menü-Namens oder der Einschränkungen.</td></tr><tr><td style="text-align:left;">Menü löschen</td><td style="text-align:left;">Löscht das Menü sowie alle Einträge und zugehörigen Seiten!</td></tr><tr><td style="text-align:left;">Seite hinzufügen</td><td style="text-align:left;">Fügt dem Menü eine Seite hinzu. Bei Linksklick auf das Menü wird die Seite direkt geöffnet.</td></tr><tr><td style="text-align:left;">Eintrag hinzufügen</td><td style="text-align:left;">Fügt dem Menü einen Eintrag hinzu. Bei Linksklick auf das Menü öffnet sich der Eintrag und zeigt seine Untereinträge.</td></tr><tr><td style="text-align:left;">Menü exportieren</td><td style="text-align:left;">Exportiert die Seiten des Menüs und deren Inhalte einschließlich der Objekte mit Mediendateien in das <code>exports</code>-Verzeichnis des Projekts.</td></tr><tr><td style="text-align:left;">Menü umhängen</td><td style="text-align:left;">Öffnet einen Dialog, um das Menü in ein anderes Menü zu verschieben.</td></tr><tr><td style="text-align:left;">Nach links verschieben</td><td style="text-align:left;">Verschiebt das Menü einen Schritt nach links.</td></tr><tr><td style="text-align:left;">Nach rechts verschieben</td><td style="text-align:left;">Verschiebt das Menü einen Schritt nach rechts.</td></tr></tbody></table><h2 id="menueintrage" tabindex="-1">Menüeinträge <a class="header-anchor" href="#menueintrage" aria-label="Permalink to &quot;Menüeinträge&quot;">​</a></h2><p>Menüs können Einträge enthalten.<br> Beim Hinzufügen eines Eintrags zu einem Menü öffnet sich derselbe Dialog wie bei Menüs, und ein Name sowie Einschränkungen für den Menüeintrag können angegeben werden.</p><p>Beim Rechtsklick auf einen bestehenden Menüeintrag öffnet sich ein Kontextmenü mit folgenden Optionen:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Beschreibung</th></tr></thead><tbody><tr><td style="text-align:left;">Menüeintrag bearbeiten</td><td style="text-align:left;">Öffnet den Dialog für den Menüeintrag und erlaubt die Änderung des Namens oder der Einschränkungen.</td></tr><tr><td style="text-align:left;">Menüeintrag löschen</td><td style="text-align:left;">Löscht den Menüeintrag und die zugehörige Seite!</td></tr><tr><td style="text-align:left;">Menüeintrag exportieren</td><td style="text-align:left;">Exportiert die Seite des Menüeintrags und deren Inhalte einschließlich der Objekte mit Mediendateien in das Projekt-<code>exports</code>-Verzeichnis.</td></tr><tr><td style="text-align:left;">Menüeintrag umhängen</td><td style="text-align:left;">Öffnet einen Dialog, um den Menüeintrag in ein anderes Menü oder das Hauptmenü zu verschieben.</td></tr><tr><td style="text-align:left;">Nach oben verschieben</td><td style="text-align:left;">Verschiebt den Menüeintrag einen Schritt nach oben.</td></tr><tr><td style="text-align:left;">Nach unten verschieben</td><td style="text-align:left;">Verschiebt den Menüeintrag einen Schritt nach unten.</td></tr></tbody></table>',14)])])}const x=t(r,[["render",s]]);export{c as __pageData,x as default};
