import{_ as t,c as n,o as r,ae as i}from"./chunks/framework.CeIuOzFV.js";const c=JSON.parse('{"title":"Exchange-Konfiguration","description":"","frontmatter":{},"headers":[],"relativePath":"de/user-manual/settings/exchange.md","filePath":"de/user-manual/settings/exchange.md"}'),a={name:"de/user-manual/settings/exchange.md"};function l(o,e,s,d,u,h){return r(),n("div",null,[...e[0]||(e[0]=[i('<h1 id="exchange-konfiguration" tabindex="-1">Exchange-Konfiguration <a class="header-anchor" href="#exchange-konfiguration" aria-label="Permalink to &quot;Exchange-Konfiguration&quot;">​</a></h1><h2 id="synchronisation" tabindex="-1">Synchronisation <a class="header-anchor" href="#synchronisation" aria-label="Permalink to &quot;Synchronisation&quot;">​</a></h2><p>Zwei Konfigurationsoptionen müssen gesetzt werden, um Sammlungsobjekte von einer lokalen Installation auf eine entfernte Instanz von Artivact hochzuladen:</p><ul><li>Remote-Artivact-Server <ul><li>HTTP(S)-URL der entfernten Artivact-Instanz, z.B. <code>https://www.beispiel.com/</code></li></ul></li><li>API-Token <ul><li>Ein Sicherheitstoken (String), das in den Kontoeinstellungen der entfernten Instanz konfiguriert wurde. Dieses Benutzerkonto wird für den Upload verwendet.</li></ul></li></ul><h2 id="content-exporte" tabindex="-1">Content-Exporte <a class="header-anchor" href="#content-exporte" aria-label="Permalink to &quot;Content-Exporte&quot;">​</a></h2><p>Content-Exporte von Teilen oder sogar der gesamten Sammlung können hier verwaltet werden.</p><p>Basis für den Content-Export ist immer ein Menü. Die zugehörige Seite und alle darauf angezeigten Sammlungsobjekte werden im Export enthalten sein.</p><p>Zusätzlich bietet die Export-Konfiguration folgende Optionen:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Beschreibung</th></tr></thead><tbody><tr><td style="text-align:left;">Einschränkungen</td><td style="text-align:left;">Content-Exporte können auf Administratoren und/oder Benutzer beschränkt werden.</td></tr><tr><td style="text-align:left;">Titel</td><td style="text-align:left;">Ein kurzer Titel des Exports, der im Export selbst enthalten sein wird.</td></tr><tr><td style="text-align:left;">Kurzbeschreibung</td><td style="text-align:left;">Eine kurze Zusammenfassung des Inhalts des Content-Exports.</td></tr><tr><td style="text-align:left;">Inhalt</td><td style="text-align:left;">Eine längere, detailliertere Beschreibung des Inhalts des Exports.</td></tr><tr><td style="text-align:left;">Exportgröße optimieren</td><td style="text-align:left;">Optimierte Exporte enthalten, falls verfügbar, ein einziges 3D-Modell für jedes Sammlungsobjekt oder als Fallback ein einzelnes Bild des Objekts, anstelle aller Mediendateien.</td></tr><tr><td style="text-align:left;">Einschränkungen ausschließen</td><td style="text-align:left;">Schließt alle eingeschränkten Sammlungsobjekte, Widgets oder Items vom Export aus.</td></tr></tbody></table><h2 id="content-importe" tabindex="-1">Content-Importe <a class="header-anchor" href="#content-importe" aria-label="Permalink to &quot;Content-Importe&quot;">​</a></h2><p>Früher erstellte Content-Exporte können in Artivact importiert werden. Für den Content-Import stehen zwei Optionen zur Verfügung:</p><ul><li>Vollständiger Import: Der Content wird einschließlich aller enthaltenen Sammlungsobjekte, Seiten und Menüs importiert.</li><li>Import zur Verteilung: Nur die Content-Export-Datei wird für die weitere Verteilung importiert, ohne die enthaltenen Sammlungsobjekte, Seiten und Menüs zu importieren.</li></ul>',12)])])}const p=t(a,[["render",l]]);export{c as __pageData,p as default};
