import{_ as a,C as i,c as o,o as l,j as t,ae as s,a as r,G as d}from"./chunks/framework.CeIuOzFV.js";const h="/artivact/assets/peripherals-configuration.89NowvVy.png",D=JSON.parse('{"title":"Peripherie-Konfiguration","description":"","frontmatter":{},"headers":[],"relativePath":"de/user-manual/settings/peripherals.md","filePath":"de/user-manual/settings/peripherals.md"}'),u={name:"de/user-manual/settings/peripherals.md"},g={id:"peripherie-konfiguration",tabindex:"-1"};function c(p,e,m,b,f,k){const n=i("Badge");return l(),o("div",null,[t("h1",g,[e[0]||(e[0]=r("Peripherie-Konfiguration ",-1)),d(n,{type:"warning",text:"desktop"}),e[1]||(e[1]=r()),e[2]||(e[2]=t("a",{class:"header-anchor",href:"#peripherie-konfiguration","aria-label":'Permalink to "Peripherie-Konfiguration <Badge type="warning" text="desktop"/>"'},"​",-1))]),e[3]||(e[3]=s('<h2 id="konfiguration" tabindex="-1">Konfiguration <a class="header-anchor" href="#konfiguration" aria-label="Permalink to &quot;Konfiguration&quot;">​</a></h2><p>Die Peripherie-Konfiguration fügt Artivact externe Drittanbieter-Software zur Erstellung von 3D-Modellen hinzu.</p><p>Sie ist nur in der Desktop-Version verfügbar.</p><p>Die Seite zeigt Konfigurationsoptionen für jeden externen Bestandteil der 3D-Modellerstellung:</p><p><img src="'+h+'" alt="peripherals-configuration"></p><p>Durch klick auf &quot;Peripheriegeräte Scannen&quot; versucht Artivact angeschlossene Geräte und installierte Software zu finden, und Konfigurationen für diese Anzulegen.</p><h2 id="drehteller" tabindex="-1">Drehteller <a class="header-anchor" href="#drehteller" aria-label="Permalink to &quot;Drehteller&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">Linux-Systeme</p><p>Linux-Nutzer müssen Mitglied der <strong>dialout</strong>-Gruppe sein, um den Artivact-Drehteller verwenden zu können!</p></div><p>Nach dem Zusammenbau des Drehtellers können Sie ihn über USB mit Ihrem PC verbinden und in Artivact konfigurieren.</p><table tabindex="0"><thead><tr><th style="text-align:center;">Auswahl</th><th style="text-align:center;">Konfigurationswert</th></tr></thead><tbody><tr><td style="text-align:center;"><code>Artivact Drehteller</code></td><td style="text-align:center;">Artivacts DIY-Drehteller</td></tr></tbody></table><h2 id="kamera" tabindex="-1">Kamera <a class="header-anchor" href="#kamera" aria-label="Permalink to &quot;Kamera&quot;">​</a></h2><p>Für die automatische Bilderfassung müssen Sie Ihre Kamera per USB an den PC anschließen und das Picture Transfer Protocol (PTP) aktivieren.<br> Wenn die Kamera nicht mit dem eingebauten PTP-Adapter funktioniert, können Sie zusätzliche Software installieren, um sie remote zu steuern.<br> Derzeit werden als Fallback <a href="https://digicamcontrol.com/" target="_blank" rel="noreferrer">DigiCamControl</a> für Windows und <a href="http://gphoto.org/" target="_blank" rel="noreferrer">gphoto2</a> für Linux unterstützt.</p><h3 id="digicamcontrol" tabindex="-1">DigiCamControl <a class="header-anchor" href="#digicamcontrol" aria-label="Permalink to &quot;DigiCamControl&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Unterstützte Version</p><p>Die DigiCamControl-Integration wurde mit Version <strong>2.1</strong> getestet.</p></div><table tabindex="0"><thead><tr><th style="text-align:left;">Auswahl</th><th style="text-align:left;">Konfigurationswert</th><th style="text-align:left;">Beispiel</th></tr></thead><tbody><tr><td style="text-align:left;"><code>DigiCamControl</code></td><td style="text-align:left;">Die ausführbare Datei der Anwendung</td><td style="text-align:left;"><code>C:/Program Files (x86)/digiCamControl/CameraControlCmd.exe</code></td></tr></tbody></table><h3 id="gphoto2" tabindex="-1">gphoto2 <a class="header-anchor" href="#gphoto2" aria-label="Permalink to &quot;gphoto2&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Unterstützte Version</p><p>Die gphoto2-Integration wurde mit Version <strong>2.5</strong> getestet.</p></div><table tabindex="0"><thead><tr><th style="text-align:left;">Auswahl</th><th style="text-align:left;">Konfigurationswert</th><th style="text-align:left;">Beispiel</th></tr></thead><tbody><tr><td style="text-align:left;"><code>gphoto2</code></td><td style="text-align:left;">Die ausführbare Datei der Anwendung</td><td style="text-align:left;"><code>/usr/bin/gphoto2</code></td></tr></tbody></table><h2 id="hintergrundentfernung" tabindex="-1">Hintergrundentfernung <a class="header-anchor" href="#hintergrundentfernung" aria-label="Permalink to &quot;Hintergrundentfernung&quot;">​</a></h2><p>Artivact kann den Hintergrund aufgenommener Bilder automatisch entfernen.<br> Hierfür werden Open-Source-Neuronale Netze zur Erkennung markanter Objekte verwendet. Die Standardkonfiguration liefert in der Regel akzeptable Ergebnisse.</p><p>Für schärfere oder bessere Resultate können neue Netzwerke heruntergeladen, im Projektverzeichnis <code>./utils/onnx/</code> abgelegt und hier konfiguriert werden.</p><p>Das freie Open-Source-Tool <a href="https://github.com/danielgatis/rembg" target="_blank" rel="noreferrer">rembg</a> von Daniel Gatis bietet eine Sammlung von Netzwerken sowie einen Vergleich auf seiner GitHub-Seite.</p><h2 id="modellerstellung" tabindex="-1">Modellerstellung <a class="header-anchor" href="#modellerstellung" aria-label="Permalink to &quot;Modellerstellung&quot;">​</a></h2><p>Die Modellerstellung mit Photogrammetrie wird von Artivact über externe Programme unterstützt.</p><h3 id="meshroom" tabindex="-1">Meshroom <a class="header-anchor" href="#meshroom" aria-label="Permalink to &quot;Meshroom&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Unterstützte Version</p><p>Die Meshroom-Integration wurde mit Version <strong>2025.1</strong> getestet.</p></div><h3 id="metashape" tabindex="-1">Metashape <a class="header-anchor" href="#metashape" aria-label="Permalink to &quot;Metashape&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Unterstützte Version</p><p>Die Metashape-Integration wurde mit Version <strong>2.2</strong> getestet.</p></div><h3 id="realityscan" tabindex="-1">RealityScan <a class="header-anchor" href="#realityscan" aria-label="Permalink to &quot;RealityScan&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Unterstützte Version</p><p>Die RealityScan-Integration wurde mit Version <strong>2.0</strong> getestet.</p></div><h2 id="modelleditor" tabindex="-1">Modelleditor <a class="header-anchor" href="#modelleditor" aria-label="Permalink to &quot;Modelleditor&quot;">​</a></h2><p>Die Modellbearbeitung wird von Artivact über eine externe Anwendung unterstützt: Blender 3D.</p><h3 id="blender-3d" tabindex="-1">Blender 3D <a class="header-anchor" href="#blender-3d" aria-label="Permalink to &quot;Blender 3D&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Unterstützte Version</p><p>Die Blender-Integration wurde mit den Versionen <strong>4.5</strong> und <strong>5.0</strong> getestet.</p></div><p>Für die Modellbearbeitung kann Blender 3D direkt aus Artivact gestartet werden.<br> Das ausgewählte Modell-Set, das ein OBJ-3D-Modell aus den vorherigen Schritten enthält, wird automatisch importiert.</p><p>Das finale 3D-Modell sollte als GLTF/GLB-Datei in den von Blender vorgeschlagenen Standardordner exportiert</p>',36))])}const v=a(u,[["render",c]]);export{D as __pageData,v as default};
