import{_ as t,a as e,b as a}from"./chunks/image-capture_bad_sharpness.U3TgGsP9.js";import{_ as o,c as r,o as i,a2 as n}from"./chunks/framework.BverbYjs.js";const _=JSON.parse('{"title":"Capturing Images","description":"","frontmatter":{},"headers":[],"relativePath":"tutorials/artivact-as-scanner/capturing-images.md","filePath":"tutorials/artivact-as-scanner/capturing-images.md"}'),s={name:"tutorials/artivact-as-scanner/capturing-images.md"},l=n('<h1 id="capturing-images" tabindex="-1">Capturing Images <a class="header-anchor" href="#capturing-images" aria-label="Permalink to &quot;Capturing Images&quot;">​</a></h1><h2 id="capturing-good-images" tabindex="-1">Capturing Good Images <a class="header-anchor" href="#capturing-good-images" aria-label="Permalink to &quot;Capturing Good Images&quot;">​</a></h2><p>Capturing images for photogrammetry is easy, but certain restrictions apply that should receive attention.</p><p>Good pictures for photogrammetry for creating virtual items meet the following conditions:</p><ul><li>Achieve good, constant lighting of the complete object, e.g. <img src="'+t+'" alt="good"></li><li>Avoid hard shadows, e.g. <img src="'+e+'" alt="hard shadows"></li><li>Avoid a low depth of sharpness, e.g. <img src="'+a+'" alt="low depth of sharpness"></li></ul><p>In order to create good results with photogrammetry software, the images of the item should have a high overlap. A rotation of 10% of the object between taking pictures is optimal. I.e. a minimum of 36 pictures should be taken (360° / 10% overlap) in order to create a good model.</p><h2 id="automatic-capturing-with-artivact" tabindex="-1">Automatic Capturing with Artivact <a class="header-anchor" href="#automatic-capturing-with-artivact" aria-label="Permalink to &quot;Automatic Capturing with Artivact&quot;">​</a></h2><p>You can import taken pictures manually into Artivact as described in the <a href="/artivact/user-manual/introduction/about.html">user manual</a>.</p><p>But the real benefit of using Artivact on the desktop is by automating the camera usage and import the images automatically.</p><p>For this, you need to attach your camera to your PC using USB and install Software to control it remotely. Currently, <a href="https://digicamcontrol.com/" target="_blank" rel="noreferrer">DigiCamControl</a> for Windows systems and <a href="http://gphoto.org/" target="_blank" rel="noreferrer">gphoto2</a> for Linux systems is supported.</p><p>After installation, you can configure the software under:</p><p><code>Settings</code> -&gt; <code>Peripherals</code> -&gt; <code>Camera Configuration</code></p><h3 id="digicamcontrol" tabindex="-1">DigiCamControl <a class="header-anchor" href="#digicamcontrol" aria-label="Permalink to &quot;DigiCamControl&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Supported Version</p><p>DigiCamControl integration has been tested for Version 2.1.</p></div><table><thead><tr><th style="text-align:center;">Selection</th><th style="text-align:center;">Configuration Value</th><th style="text-align:center;">Example</th></tr></thead><tbody><tr><td style="text-align:center;"><code>DigiCamControl</code></td><td style="text-align:center;">The application&#39;s executable</td><td style="text-align:center;"><code>C:/Program Files (x86)/digiCamControl/CameraControlCmd.exe</code></td></tr><tr><td style="text-align:center;"><code>DigiCamControl (Remote)</code></td><td style="text-align:center;">The application&#39;s web endpoint, if the <a href="https://digicamcontrol.com/doc/userguide/web" target="_blank" rel="noreferrer">web interface</a> is enabled.</td><td style="text-align:center;"><code>http://localhost:5513/</code></td></tr></tbody></table><p>&quot;DigiCamControl (Remote)&quot; provides better performance, but needs configuration in DigiCamControl to work. The embedded web server needs to be activated by the user: <a href="https://digicamcontrol.com/doc/userguide/settings#webserver" target="_blank" rel="noreferrer">DigiCamControl Webserver Configuration</a></p><h3 id="gphoto2" tabindex="-1">gphoto2 <a class="header-anchor" href="#gphoto2" aria-label="Permalink to &quot;gphoto2&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Supported Version</p><p>gphoto2 integration has been tested for Version 2.5.</p></div><table><thead><tr><th style="text-align:center;">Selection</th><th style="text-align:center;">Configuration Value</th><th style="text-align:center;">Example</th></tr></thead><tbody><tr><td style="text-align:center;"><code>gphoto2</code></td><td style="text-align:center;">The application&#39;s executable</td><td style="text-align:center;"><code>/usr/bin/gphoto2</code></td></tr></tbody></table>',19),c=[l];function d(g,h,p,m,u,b){return i(),r("div",null,c)}const y=o(s,[["render",d]]);export{_ as __pageData,y as default};
