import{_ as e,c as n,o as a,ae as l}from"./chunks/framework.CeIuOzFV.js";const i="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAADgAAAAkCAIAAABT8G6pAAAAf0lEQVR4nOzUoQ2AMBBG4YouAYKRECQYgmULNkChsQRDgmAbDGEUBK6VPHO5/5OnXnrpxXGagwUxGKFQmkJpCqUplOY4dOjaqizW47zuJ3C0eppCaW5Cvz+ez/umTiY/74CbF122PZnojhqhUJrj0PwOILR6mkJpCqUplKZQ2gsAAP//ygcWWQAAAAZJREFUAwByzRj9rzQQkgAAAABJRU5ErkJggg==",s="data:image/png;base64,",f=JSON.parse('{"title":"Menus","description":"","frontmatter":{},"headers":[],"relativePath":"user-manual/content-management/menus.md","filePath":"user-manual/content-management/menus.md"}'),d={name:"user-manual/content-management/menus.md"};function r(o,t,g,h,u,p){return a(),n("div",null,[...t[0]||(t[0]=[l('<h1 id="menus" tabindex="-1">Menus <a class="header-anchor" href="#menus" aria-label="Permalink to &quot;Menus&quot;">​</a></h1><h2 id="menu-management" tabindex="-1">Menu Management <a class="header-anchor" href="#menu-management" aria-label="Permalink to &quot;Menu Management&quot;">​</a></h2><p>You can add menus by left-clicking on the following button in the top bar: <img src="'+i+'" alt="add-menu-button"></p><p>Menus can only be added with the default locale selected!</p><p>A menu opens, offering the following options: <img src="'+s+'" alt="add-menu-menu"></p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">Create Menu</td><td style="text-align:left;">Opens a dialog to create a new menu.</td></tr><tr><td style="text-align:left;">Import Menu</td><td style="text-align:left;">Imports a previously exported menu into the menu bar.</td></tr></tbody></table><p>The dialog for creating/editing a menu offers the following options:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">Menu</td><td style="text-align:left;">The menu&#39;s name which is shown in the menu bar.</td></tr><tr><td style="text-align:left;">Restrictions</td><td style="text-align:left;">Menus can be restricted to administrators and/or users. The menu is not shown unless the authenticated user has the proper role.</td></tr><tr><td style="text-align:left;">External Page URL</td><td style="text-align:left;">A menu can point to an external URL instead of a page.</td></tr><tr><td style="text-align:left;">External Page URL</td><td style="text-align:left;">A menu can point to an external URL instead of a page.</td></tr><tr><td style="text-align:left;">URL Alias</td><td style="text-align:left;">When a menu points to an Artivact page, and not an external URL, an alias can be defined. The alias can be used in the browser&#39;s URL instead of the page&#39;s ID to navigate to the page.</td></tr><tr><td style="text-align:left;">URL Alias</td><td style="text-align:left;">When a menu points to an Artivact page, and not an external URL, an alias can be defined. The alias can be used in the browser&#39;s URL instead of the page&#39;s ID to navigate to the page.</td></tr><tr><td style="text-align:left;">Hidden Menu</td><td style="text-align:left;">A menu and its associated page can be made publicly available, i.e. without any restrictions, but hidden from the menu bar with this option.</td></tr></tbody></table><p>By right-clicking on the menu a context menu opens, which offers the following options:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">Edit Menu</td><td style="text-align:left;">Opens the menu dialog and lets you change the menu&#39;s name or restrictions.</td></tr><tr><td style="text-align:left;">Delete Menu</td><td style="text-align:left;">Deletes the menu and all its entries and associated pages!</td></tr><tr><td style="text-align:left;">Add Page</td><td style="text-align:left;">Adds a page to the menu. When left-clicking the menu, the page will open directly.</td></tr><tr><td style="text-align:left;">Add Entry</td><td style="text-align:left;">Adds an entry to the menu. When left-clicking the menu, it will open and show its menu entries.</td></tr><tr><td style="text-align:left;">Export Menu</td><td style="text-align:left;">Exports the menu&#39;s pages and their content including items with media files into the project&#39;s <code>exports</code> directory.</td></tr><tr><td style="text-align:left;">Relocate Menu</td><td style="text-align:left;">Opens a dialog to move the menu into another menu.</td></tr><tr><td style="text-align:left;">Move Left</td><td style="text-align:left;">Moves the menu one step to the left.</td></tr><tr><td style="text-align:left;">Move Right</td><td style="text-align:left;">Moves the menu one step to the right.</td></tr></tbody></table><h2 id="menu-entries" tabindex="-1">Menu Entries <a class="header-anchor" href="#menu-entries" aria-label="Permalink to &quot;Menu Entries&quot;">​</a></h2><p>Menus can have entries. When adding an entry to a menu, the same dialog as the one for menus opens, and a menu entry name and restrictions can be specified.</p><p>When right-clicking on an existing menu entry, a context menu opens, which offers the following options:</p><table tabindex="0"><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">Edit Menu Entry</td><td style="text-align:left;">Opens the menu entry dialog and lets you change the menu entry&#39;s name or restrictions.</td></tr><tr><td style="text-align:left;">Delete Menu Entry</td><td style="text-align:left;">Deletes the menu entry and its associated page!</td></tr><tr><td style="text-align:left;">Export Content</td><td style="text-align:left;">Exports the menu&#39;s page and its content including items with media files into the project&#39;s <code>exports</code> directory.</td></tr><tr><td style="text-align:left;">Relocate Menu Entry</td><td style="text-align:left;">Opens a dialog to move the menu entry into another menu or the main menu.</td></tr><tr><td style="text-align:left;">Move Up</td><td style="text-align:left;">Moves the menu entry one step up.</td></tr><tr><td style="text-align:left;">Move Down</td><td style="text-align:left;">Moves the menu one step down.</td></tr></tbody></table>',14)])])}const y=e(d,[["render",r]]);export{f as __pageData,y as default};
