import{_ as t}from"./chunks/add-menu-button.Bcn7dgJX.js";import{_ as e,c as n,o as a,a2 as i}from"./chunks/framework.DSgG5TdZ.js";const s="/artivact/assets/add-menu-modal.BscpEFex.png",l="data:image/png;base64,",o="/artivact/assets/menu-context-menu-Middle.CAJKbKrm.png",d="/artivact/assets/menu-entry-context-menu-Beta.CHaJTYym.png",x=JSON.parse('{"title":"Menus","description":"","frontmatter":{},"headers":[],"relativePath":"user-manual/content-management/menus.md","filePath":"user-manual/content-management/menus.md"}'),r={name:"user-manual/content-management/menus.md"},m=i('<h1 id="menus" tabindex="-1">Menus <a class="header-anchor" href="#menus" aria-label="Permalink to &quot;Menus&quot;">​</a></h1><h2 id="menu-management" tabindex="-1">Menu Management <a class="header-anchor" href="#menu-management" aria-label="Permalink to &quot;Menu Management&quot;">​</a></h2><p>You can add menus by left-clicking on the following button in the top bar: <img src="'+t+'" alt="add-menu-button"></p><p>A dialog opens, and you can configure the menu&#39;s name and restrictions, if the menu shouldn&#39;t be available to anyone:</p><p><img src="'+s+'" alt="add-menu-modal"></p><p>Additionally, you can provide a name and description for exports based on this menu. Exports of menus contain all pages of the menu and their contained items with media files.</p><p>After adding the menu, it will be shown in the menu bar. As long as there are no menu entries defined, or a page assigned to the menu, it will be shown in red and only to administrators.</p><p><img src="'+l+'" alt="artivact-menu-bar"></p><p>By right-clicking on the menu a context menu opens</p><p><img src="'+o+'" alt="menu-context-menu"></p><p>which offers the following options:</p><table><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">Add Page</td><td style="text-align:left;">Adds a page to the menu. When left-clicking the menu, the page will open directly.</td></tr><tr><td style="text-align:left;">Add Entry</td><td style="text-align:left;">Adds an entry to the menu. When left-clicking the menu, it will open and show its menu entries.</td></tr><tr><td style="text-align:left;">Edit Menu</td><td style="text-align:left;">Opens the menu dialog and lets you change the menu&#39;s name or restrictions.</td></tr><tr><td style="text-align:left;">Export Content</td><td style="text-align:left;">Exports the menu&#39;s pages and their content including items with media files into the project&#39;s <code>exports</code> directory.</td></tr><tr><td style="text-align:left;">Delete Menu</td><td style="text-align:left;">Deletes the menu and all its entries and associated pages!</td></tr><tr><td style="text-align:left;">Move Left</td><td style="text-align:left;">Moves the menu one step to the left.</td></tr><tr><td style="text-align:left;">Move Right</td><td style="text-align:left;">Moves the menu one step to the right.</td></tr></tbody></table><h2 id="menu-entries" tabindex="-1">Menu Entries <a class="header-anchor" href="#menu-entries" aria-label="Permalink to &quot;Menu Entries&quot;">​</a></h2><p>Menus can have entries. When adding an entry to a menu, the same dialog as the one for menus opens, and a menu entry name and restrictions can be specified.</p><p>When right-clicking on an existing menu entry, a context menu opens</p><p><img src="'+d+'" alt="menu-entry-context-menu"></p><p>which offers the following options:</p><table><thead><tr><th style="text-align:left;">Option</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;">Edit Menu Entry</td><td style="text-align:left;">Opens the menu entry dialog and lets you change the menu entry&#39;s name or restrictions.</td></tr><tr><td style="text-align:left;">Delete Menu Entry</td><td style="text-align:left;">Deletes the menu entry and its associated page!</td></tr><tr><td style="text-align:left;">Export Content</td><td style="text-align:left;">Exports the menu&#39;s page and its content including items with media files into the project&#39;s <code>exports</code> directory.</td></tr><tr><td style="text-align:left;">Move Up</td><td style="text-align:left;">Moves the menu entry one step up.</td></tr><tr><td style="text-align:left;">Move Down</td><td style="text-align:left;">Moves the menu one step down.</td></tr></tbody></table>',18),h=[m];function p(u,c,g,k,f,S){return a(),n("div",null,h)}const E=e(r,[["render",p]]);export{x as __pageData,E as default};
