import{_ as r,C as i,c as n,o as l,j as t,ae as s,a,G as d}from"./chunks/framework.CeIuOzFV.js";const c="/artivact/assets/peripherals-configuration.CT5SmBR4.png",k=JSON.parse('{"title":"Peripherals Configuration","description":"","frontmatter":{},"headers":[],"relativePath":"user-manual/settings/peripherals.md","filePath":"user-manual/settings/peripherals.md"}'),p={name:"user-manual/settings/peripherals.md"},h={id:"peripherals-configuration",tabindex:"-1"};function u(g,e,m,b,f,y){const o=i("Badge");return l(),n("div",null,[t("h1",h,[e[0]||(e[0]=a("Peripherals Configuration ",-1)),d(o,{type:"warning",text:"desktop"}),e[1]||(e[1]=a()),e[2]||(e[2]=t("a",{class:"header-anchor",href:"#peripherals-configuration","aria-label":'Permalink to "Peripherals Configuration <Badge type="warning" text="desktop"/>"'},"​",-1))]),e[3]||(e[3]=s('<h2 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-label="Permalink to &quot;Configuration&quot;">​</a></h2><p>Peripheral configuration adds the external 3rd party software to create 3D models to Artivact.</p><p>It is only available in the desktop version.</p><p>The page shows configuration options for every external part of 3D model creation:</p><p><img src="'+c+'" alt="peripherals-configuration"></p><p>By clicking on &quot;Scan Peripherals&quot;, Artivact attempts to detect connected devices and installed software and create configurations for them.</p><h2 id="turntable" tabindex="-1">Turntable <a class="header-anchor" href="#turntable" aria-label="Permalink to &quot;Turntable&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">Linux Systems</p><p>Linux users have to be in the <strong>dialout</strong> group in order to use the Artivact turntable!</p></div><p>After assembly of the turntable, you can connect it to your PC via USB and configure it in Artivact.</p><table tabindex="0"><thead><tr><th style="text-align:center;">Selection</th><th style="text-align:center;">Configuration Value</th></tr></thead><tbody><tr><td style="text-align:center;"><code>Artivact Turntable</code></td><td style="text-align:center;">Artivact&#39;s DIY turntable</td></tr></tbody></table><h2 id="camera" tabindex="-1">Camera <a class="header-anchor" href="#camera" aria-label="Permalink to &quot;Camera&quot;">​</a></h2><p>For automatic image capturing, you need to attach your camera to your PC using USB and enable Picture Transfer Protocol (PTP) on it. If the camera is not working with the builtin PTP peripheral adapter, you can install additional Software to control it remotely. Currently, <a href="https://digicamcontrol.com/" target="_blank" rel="noreferrer">DigiCamControl</a> for Windows systems and <a href="http://gphoto.org/" target="_blank" rel="noreferrer">gphoto2</a> for Linux systems are supported as fallback.</p><h3 id="digicamcontrol" tabindex="-1">DigiCamControl <a class="header-anchor" href="#digicamcontrol" aria-label="Permalink to &quot;DigiCamControl&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Supported Version</p><p>DigiCamControl integration has been tested for Version <strong>2.1</strong>.</p></div><table tabindex="0"><thead><tr><th style="text-align:left;">Selection</th><th style="text-align:left;">Configuration Value</th><th style="text-align:left;">Example</th></tr></thead><tbody><tr><td style="text-align:left;"><code>DigiCamControl</code></td><td style="text-align:left;">The application&#39;s executable</td><td style="text-align:left;"><code>C:/Program Files (x86)/digiCamControl/CameraControlCmd.exe</code></td></tr></tbody></table><h3 id="gphoto2" tabindex="-1">gphoto2 <a class="header-anchor" href="#gphoto2" aria-label="Permalink to &quot;gphoto2&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Supported Version</p><p>gphoto2 integration has been tested for Version <strong>2.5</strong>.</p></div><table tabindex="0"><thead><tr><th style="text-align:left;">Selection</th><th style="text-align:left;">Configuration Value</th><th style="text-align:left;">Example</th></tr></thead><tbody><tr><td style="text-align:left;"><code>gphoto2</code></td><td style="text-align:left;">The application&#39;s executable</td><td style="text-align:left;"><code>/usr/bin/gphoto2</code></td></tr></tbody></table><h2 id="background-removal" tabindex="-1">Background Removal <a class="header-anchor" href="#background-removal" aria-label="Permalink to &quot;Background Removal&quot;">​</a></h2><p>Artivact can automatically remove the background of captured images. For this, open-source neural networks for salient object detection are used. The default configuration should provide acceptable results.</p><p>If sharper / better results are needed, you can download new networks, place them in the project&#39;s <code>./utils/onnx/</code> directory and configure their parameters here.</p><p>The free and open-source tool <a href="https://github.com/danielgatis/rembg" target="_blank" rel="noreferrer">rembg</a> by Daniel Gatis offers a collection of networks and a comparison of them on his project&#39;s Github page.</p><h2 id="model-creator" tabindex="-1">Model Creator <a class="header-anchor" href="#model-creator" aria-label="Permalink to &quot;Model Creator&quot;">​</a></h2><p>Model creation with photogrammetry is supported by Artivact through external programs.</p><h3 id="meshroom" tabindex="-1">Meshroom <a class="header-anchor" href="#meshroom" aria-label="Permalink to &quot;Meshroom&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Supported Version</p><p>Meshroom integration has been tested for Version <strong>2025.1</strong>.</p></div><h3 id="metashape" tabindex="-1">Metashape <a class="header-anchor" href="#metashape" aria-label="Permalink to &quot;Metashape&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Supported Version</p><p>Metashape integration has been tested for Version <strong>2.2</strong></p></div><h3 id="realityscan" tabindex="-1">RealityScan <a class="header-anchor" href="#realityscan" aria-label="Permalink to &quot;RealityScan&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Supported Version</p><p>RealityScan integration has been tested for Version <strong>2.0</strong></p></div><h2 id="model-editor" tabindex="-1">Model Editor <a class="header-anchor" href="#model-editor" aria-label="Permalink to &quot;Model Editor&quot;">​</a></h2><p>Model editing is supported by Artivact through an external application: Blender 3D.</p><h3 id="blender-3d" tabindex="-1">Blender 3D <a class="header-anchor" href="#blender-3d" aria-label="Permalink to &quot;Blender 3D&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">Supported Version</p><p>Blender integration has been tested for Versions <strong>4.5</strong> and <strong>5.0</strong>.</p></div><p>For model editing, Blender 3D can be opened from within Artivact. The selected model-set, containing an OBJ 3D model from the previous steps, will automatically be imported.</p><p>The final 3D model should be exported as GLTF/GLB file into the default folder suggested by Blender.</p><p>It can then finally be transferred to the virtual items media section and used from there on.</p>',37))])}const v=r(p,[["render",u]]);export{k as __pageData,v as default};
